#[derive(Debug)]
pub struct FunctionManager {
    pub count: usize,
    pub func_name: Vec<String>,
    pub func_args: Vec<Vec<String>>,
    pub func_signature: Vec<Vec<Type>>,
    pub func_out_type: Vec<Type>,
    pub func_body: Vec<Sexpr>,
    pub func_labels: Vec<String>, // map the definition id's to their labels
    pub call_labels: Vec<String>,
    label_counter: usize,
}
impl FunctionManager {
    pub fn new() -> FunctionManager {
        FunctionManager {
            count: 0,
            func_name: vec![],
            func_args: vec![],
            func_signature: vec![],
            func_out_type: vec![],
            func_body: vec![],
            func_labels: vec![],
            call_labels: vec![],
            label_counter: 0,
        }
    }

    /// call a function which needs to the manager on a body Sexpr which the manager owns
    /// crucial: this function replaces the body at id with a dummy, and then swaps it back
    pub fn apply_self_mut<T>(&mut self, id: usize, mut closure: impl FnMut(&mut Sexpr, &mut FunctionManager) -> T) -> T {
        use std::mem;
        let mut x = mem::replace(&mut self.func_body[id], Sexpr::dummy());
        let res = closure(&mut x, self);
        self.func_body[id] = x;
        res
    }


    /// declare a function with the following properties
    /// return its id
    pub fn declare_func(&mut self, name: String, arguments: Vec<String>, signature: Vec<Type>, out_type: Type, body: Sexpr) -> Result<usize, Error> {
        let id = self.count;
        self.func_name.push(name);
        self.func_args.push(arguments);
        self.func_signature.push(signature);
        self.func_out_type.push(out_type);
        self.func_body.push(body);
        let lbl = self.next_label()?;
        self.func_labels.push(lbl);
        self.count += 1;
        Ok(id)
    }
    /// declare a call site that calls the function with the given id
    /// return the call site id (to be used by the functions to return) and the label of that call site
    pub fn declare_call_site(&mut self, _id: usize) -> Result<usize, Error> {
        //TODO id can be used in optimizing
        let label = self.next_label()?;
        let call_site_id = self.call_labels.len();
        self.call_labels.push(label.clone());
        Ok(call_site_id)
    }

    ///retrieve the next unused label, returning the appropriate error if it doesn't exist
    fn next_label(&mut self) -> Result<String, Error> {
        let res =
            LABELS
            .get(self.label_counter)
            .ok_or(Error::new_zero("you have hit the label maximum (702). This compiler uses a label for each function you define and for each place you call a function".to_owned()))
            .map(|lbl| String::from(*lbl) );
        self.label_counter += 1;
        res
    }


}